odoo.template:
/* Service Worker for Progressive Web App */
importScripts('/website_seo_settings/static/js/cache-polyfill.js');
var CACHE_NAME = ''

    console.log("SW CACHE_NAME: ", CACHE_NAME);

/* Service working installation */
self.addEventListener('install', function(e) {
    e.waitUntil(
        caches.open(CACHE_NAME).then(function(cache) {

                console.log("SW Open cache: ", CACHE_NAME);

            return cache.addAll([
                ''
            ]);
        })
    );
});

/* Service worker updates */
self.addEventListener('activate', function(event) {
    var cacheWhitelist = [''];

        console.log("SW Activating new cache: ", cacheWhitelist);

    event.waitUntil(
        caches.keys().then(function(cacheNames) {
            return Promise.all(
                cacheNames.map(function(cacheName) {
                    if (cacheWhitelist.indexOf(cacheName) === -1) {

                            console.log("SW Deleted cache: ", cacheName);
                            return caches.delete(cacheName);

                    }
                })
            );
        })
    );
});

/* Service worker fetching events */
self.addEventListener('fetch', function(event) {
    event.respondWith(
        caches.match(event.request).then(function(response) {
            // Cache hit - return response
            if (response) {

                    console.log('SW Fetch resource found in cache: ', event.request.url);

              return response;
            }
            // IMPORTANT: Clone the request. A request is a stream and
            // can only be consumed once. Since we are consuming this
            // once by cache and once by the browser for fetch, we need
            // to clone the response.
            var fetchRequest = event.request.clone();
            return fetch(fetchRequest).then(
                function(response) {
                    // Check if we received a valid response
                    if(!response || response.status !== 200 || response.type !== 'basic') {

                            console.log('SW Fetch NO valid response: ', event.request.url);

                        return response;
                    }
                    // IMPORTANT: Clone the response. A response is a stream
                    // and because we want the browser to consume the response
                    // as well as the cache consuming the response, we need
                    // to clone it so we have two streams.
                    var responseToCache = response.clone();
                    caches.open(CACHE_NAME).then(function(cache) {
                        if (event.request.url.indexOf('/longpolling/poll') === -1 || event.request.url.indexOf('/call_kw') === -1){

                                console.log("SW Caching new resource: ", event.request.url);

                            cache.put(event.request.url, responseToCache);
                        }
                    });
                    return response;
                }, function(error) {
                    // fetch failed

                        console.log('ServiceWorker fetch failed: ', error);

                }
            ).catch(function(error) {
                 // fetch failed

                    console.log('ServiceWorker fetch failed: ', error);

            });
        })
    );
});
